import React from 'react'
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'
import AppBar from 'material-ui/AppBar'
import TextField from 'material-ui/TextField'
import RaisedButton from 'material-ui/RaisedButton'


function ServiceInfoForm({...props}) {

    const continueToNext = e => {
        e.preventDefault()
        props.nextStep()
    }
    const {serviceInfo, handleChangeService} = props

    return (
        <MuiThemeProvider>
            <div>
                <AppBar title= "Enter Service Information"></AppBar>
                <TextField
                    hintText = "Choose Location"
                    floatingLabelText = "Location"
                    onChange={handleChangeService('location')}
                    defaultValue={serviceInfo.location}
                />
                <br/>
                <TextField
                    hintText = "Choose Service"
                    floatingLabelText = "Service"
                    onChange={handleChangeService('name')}
                    defaultValue={serviceInfo.name}
                />
                <br/>
                <TextField
                    hintText = "Select Day"
                    floatingLabelText = "Day"
                    onChange={handleChangeService('day')}
                    defaultValue={serviceInfo.day}
                /> 
                <br/>
                <RaisedButton
                    label="Continue"
                    primary={true}
                    style={styles.button}
                    onClick={continueToNext}
                />
                <RaisedButton
                    label="Continue"
                    primary={true}
                    style={styles.button}
                    onClick={continueToNext}
                />               
            </div>
        </MuiThemeProvider>
    )
}

const styles = {
    button: {
        margin: 15
    }
}
export default ServiceInfoForm
