import React from 'react'
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider'
import AppBar from 'material-ui/AppBar'
import TextField from 'material-ui/TextField'
import RaisedButton from 'material-ui/RaisedButton'
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';

function ServiceInfoForm({...props}) {

    const continueToNext = e => {
        e.preventDefault()
        props.nextStep()
    }
    const goToPrevious = e => {
        e.preventDefault()
        props.prevStep()
    }   
    const {serviceInfo, handleChangeService} = props

    return (
        <MuiThemeProvider>
            <div>
                <AppBar title= "Enter Service Information"></AppBar>
                <Select
                    autoWidth
                    labelId="Location"
                    id="Location"
                    value={serviceInfo.location}
                    onChange={handleChangeService('location')}
                    label="Location"
                    >
                    <MenuItem value={"boulder"}>boulder</MenuItem>
                    <MenuItem value={"superior"}>superior</MenuItem>
                    <MenuItem value={"broomfield"}>broomfield</MenuItem>
                </Select>
                <br/>
                <TextField
                    hintText = "Choose Service"
                    floatingLabelText = "Service"
                    onChange={handleChangeService('name')}
                    defaultValue={serviceInfo.name}
                />
                <br/>
                <TextField
                    hintText = "Select Day"
                    floatingLabelText = "Day"
                    onChange={handleChangeService('day')}
                    defaultValue={serviceInfo.day}
                /> 
                <br/>
                <RaisedButton
                    label="Back"
                    style={styles.button}
                    onClick={goToPrevious}
                /> 
                <RaisedButton
                    label="Continue"
                    primary={true}
                    style={styles.button}
                    onClick={continueToNext}
                />              
            </div>
        </MuiThemeProvider>
    )
}

const styles = {
    button: {
        margin: 15
    }
}
export default ServiceInfoForm
