import React, {useState} from 'react'
import ClientInfoForm from './ClientInfoForm'
import ServiceInfoForm from './ServiceInfoForm'
import AvailabilityDetails from './AvailabilityDetails'
import Success from './Success'

export default function Reservation() {
    const [step, setStep] = useState(1)
    const [clientInfo, setClientInfo] = useState({
        first_name: '',
        last_name: '',
        phone_number: '',
        email: '',
    })
    const [serviceInfo, setServiceInfo] = useState({
        name: '',
        location: '',
        day: '',      
    })

    const nextStep = () => {
        setStep(prevStep => {
            return prevStep+1
        })
    }

    const prevStep = () => {
        setStep(prevStep => {
            return prevStep-1
        })
    }

    const handleChangeClient = input => e => {
        setClientInfo(prevState => {
            return {
                ...prevState,
                [input]: e.target.value,
            }
        })
    }

    const handleChangeService = input => e => {
        setServiceInfo(prevState => {
            return {
                ...prevState,
                [input]: e.target.value,
            }
        })

    }

    // const {first_name, last_name, phone_number, email} = clientInfo
    // const {name, location, day} = serviceInfo

    switch(step) {
        case 1:
            return (
                <ClientInfoForm
                    nextStep = {nextStep}
                    handleChangeClient = {handleChangeClient}
                    clientInfo = {clientInfo}
                    serviceInfo = {serviceInfo}
                />
            )
        case 2:
            return (
                <ServiceInfoForm
                    nextStep = {nextStep}
                    prevStep = {prevStep}
                    handleChangeService = {handleChangeService}
                    clientInfo = {clientInfo}
                    serviceInfo = {serviceInfo}
                />
            )
        case 3:
            return "availList"
        case 4:
            return "success"
    }
    return (
        <div>

        </div>
    )
}
